<style>
    h1 {
        font-size: 14px;
        font-weight: 600;
        color: var(--gray12);
    }

    p {
        font-size: 14px;
        font-weight: normal;
        color: var(--gray11);
        text-align: center;
    }

    button {
        background-color: var(--gray3);
        color: var(--gray12);
        border: 1px solid var(--gray4);
        border-radius: 6px;

        width: 100%;
        height: 32px;

        font-size: 13px;
        cursor: pointer;
        outline: none;

        margin-top: 8px;

        display: flex;
        flex-direction: row;
        gap: 6px;
        align-items: center;
        justify-content: center;

        transition: border 0.1s cubic-bezier(.56,.38,0,.99), transform 0.1s cubic-bezier(.56,.38,0,.99)
    }

    button:hover {
        border: 1px solid var(--gray6);
    }

    button:active {
        transform: scale(0.97);
    } 

    div.login {
        background: var(--gray2);
        border: 1px solid var(--gray3);
        border-radius: 6px;
        
        width: 273px;
        height: fit-content;

        display: flex;
        flex-direction: column;
        align-items: center;
        padding: 16px;
        gap: 8px;
    }
</style>